import React, { useState } from "react";
import { Box, Tabs, Tab, Button } from "@mui/material";
import { Grid } from "@mui/material";
import { useNavigate } from "react-router-dom";

export const Nav = () => {
  const navigate = useNavigate();

  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleSet = () => {
    navigate("/setting");
  };

  const handleLogout = () => {
    navigate("/");
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={0.5}></Grid>
      <Grid item xs={4.5}>
        <h2
          style={{
            marginTop: "10px",
            fontFamily: "cursive",
          }}
        >
          <span style={{ color: "#2db300" }}>Medi</span>{" "}
          <span style={{ color: "#4287f5" }}>Pharma</span>
        </h2>
      </Grid>
      <Grid item xs={7}>
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="basic tabs example"

          //   sx={{ justifyContent: "flex-end", display: "flex" }} // Added styles here
        >
          <Tab label="POS" sx={{ color: "green" }} />
          <Tab label="+Add Products" sx={{ color: "green" }} />
          <Tab label="+Add Category" sx={{ color: "green" }} />
          <Tab label="Settings" sx={{ color: "green" }} onClick={handleSet} />
          <Tab label="Reports " sx={{ color: "green" }} />
          <Button
            sx={{ borderRadius: "50px" }}
            onClick={handleLogout}
            variant="contained"
            color="error"
          >
            Logout{" "}
          </Button>
        </Tabs>
      </Grid>
    </Grid>
  );
};
