        </Grid>
        <Grid item xs={2}>
          <b>Desserts</b>
        </Grid>
        <Grid item xs={2}>
          <b>Beverage</b>
        </Grid>
        {items.map((item, index) => (
          <Grid item xs={3} key={index}>
            <Card style={{ height: 218 }} onClick={() => handleClick(item)}>
              <CardContent>
                <img
                  src={item.image}
                  alt={item.name}
                  height={100}
                  width={100}
                />
                <p>{item.name}</p>
                <p>${item.price}/-</p>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};
