import React, { useState } from "react";
import { Box, Tabs, Tab } from "@mui/material";
import { Grid } from "@mui/material";

export const Nav = () => {
  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={5.8}>
        <h2>
          <span>Medi</span> <span>Pharma</span>
        </h2>
      </Grid>
      <Grid item xs={6.2}>
        <Box sx={{ width: "100%" }}>
          {/* <Box sx={{ borderBottom: 1, borderColor: "divider" }}> */}
          <Tabs
            value={value}
            onChange={handleChange}
            aria-label="basic tabs example"
            sx={{ justifyContent: "flex-end", display: "flex" }} // Added styles here
          >
            <Tab label="POS" sx={{ color: "green" }} />
            <Tab label="+Add Products" sx={{ color: "green" }} />
            <Tab label="+Add Category" sx={{ color: "green" }} />
            <Tab label="Settings" sx={{ color: "green" }} />
            <Tab label="Reports " sx={{ color: "green" }} />
          </Tabs>
        </Box>
        {/* </Box> */}
      </Grid>
    </Grid>
  );
};
