import React from "react";
import { useNavigate } from "react-router-dom";
import { Button, Card, CardContent, Grid, TextField } from "@mui/material";
import { Nav } from "./Nav";
// import { Products } from "./Products";

import { Fetching } from "./Fetching";
import { Bill } from "./Bill";
import { Selectbar } from "./Selectbar";
import { Search, InputAdornment } from "@mui/icons-material";

const [data, setData] = useState([]);
const [searchQuery, setSearchQuery] = useState("");
const [selectedItems, setSelectedItems] = useState([]);

// Fetch data from the API
const getData = async () => {
  try {
    const result = await axios.get("https://fakestoreapi.com/products");
    setData(result.data);
  } catch (error) {
    console.error("Error fetching data:", error);
  }
};

useEffect(() => {
  getData();
}, []);

// Handle change in search input
const handleSearchChange = (event) => {
  setSearchQuery(event.target.value);
};

// Handle image click to add item to the billing section
const handleImageClick = (item) => {
  setSelectedItems((prevItems) => {
    const existingItem = prevItems.find((i) => i.id === item.id);
    if (existingItem) {
      return prevItems.map((i) =>
        i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
      );
    } else {
      return [
        ...prevItems,
        { id: item.id, category: item.category, quantity: 1 },
      ];
    }
  });
};

// Filter data based on search query
const filteredData = data.filter((item) =>
  item.category.toLowerCase().includes(searchQuery.toLowerCase())
);

export const Login = () => {
  return (
    <Card>
      <CardContent>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <Nav />
          </Grid>
          <Grid item xs={6}>
            <Grid container spacing={2}>
              <Grid item xs={6}>
                <TextField
                  label="Search by category"
                  variant="outlined"
                  value={searchQuery}
                  onChange={handleSearchChange}
                  sx={{
                    marginBottom: "20px",
                    width: "100%",
                    "& .MuiOutlinedInput-root": {
                      borderRadius: "20px",
                    },
                  }}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <Search />
                      </InputAdornment>
                    ),
                  }}
                />
              </Grid>
              <Grid item xs={6}>
                <Selectbar />
              </Grid>
              <Grid item xs={12}>
                <Grid container spacing={2}>
                  {filteredData.map((item) => (
                    <Grid item xs={3} key={item.id}>
                      <Card
                        sx={{ height: "200px", backgroundColor: "#f2f2f2" }}
                        onClick={() => handleImageClick(item)}
                      >
                        <CardContent>
                          <img
                            src={item.image}
                            alt={item.title}
                            height={100}
                            width={100}
                          />
                          <h4>{item.category}</h4>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
                {/* <Bill
          selectedItems={selectedItems}
          setSelectedItems={setSelectedItems}
        /> */}
              </Grid>
            </Grid>
          </Grid>
          <Grid item xs={6} style={{ border: "1px solid blue" }}>
            <Bill />
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};
