import React, { useEffect, useState } from "react";
import {
  CardContent,
  Card,
  Grid,
  TextField,
  InputAdornment,
  IconButton,
} from "@mui/material";
import { Search, Add, Remove, Delete } from "@mui/icons-material";
import axios from "axios";

export const Fetching = () => {
  const [data, setData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedItem, setSelectedItem] = useState(null);

  // Fetch data from the API
  const getData = async () => {
    try {
      const result = await axios.get("https://fakestoreapi.com/products");
      setData(result.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  // Handle change in search input
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Handle product click
  const handleProductClick = (item) => {
    setSelectedItem({ ...item, quantity: 1 });
  };

  // Handle quantity change
  const handleQuantityChange = (change) => {
    if (selectedItem) {
      setSelectedItem((prevItem) => ({
        ...prevItem,
        quantity: Math.max(prevItem.quantity + change, 1),
      }));
    }
  };

  // Handle delete
  const handleDelete = () => {
    setSelectedItem(null);
  };

  // Filter data based on search query
  const filteredData = data.filter((item) =>
    item.category.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <Grid container spacing={2}>
      {/* Product Display Grid */}
      <Grid item xs={6}>
        <TextField
          label="Search by category"
          variant="outlined"
          value={searchQuery}
          onChange={handleSearchChange}
          sx={{
            marginBottom: "20px",
            width: "100%",
            "& .MuiOutlinedInput-root": {
              borderRadius: "20px",
            },
          }}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <Search />
              </InputAdornment>
            ),
          }}
        />
        <Grid container spacing={2}>
          {filteredData.map((item) => (
            <Grid item xs={12} key={item.id}>
              <Card
                sx={{ height: "200px", backgroundColor: "#f2f2f2" }}
                onClick={() => handleProductClick(item)}
              >
                <CardContent>
                  <img
                    src={item.image}
                    alt={item.title}
                    height={100}
                    width={100}
                  />
                  <h4>{item.category}</h4>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Grid>

      {/* Selected Item Display Grid */}
      <Grid item xs={6}>
        <h3>Selected Product</h3>
        {selectedItem ? (
          <Card sx={{ backgroundColor: "#e0e0e0", padding: "10px" }}>
            <CardContent
              sx={{
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
              }}
            >
              <div>
                <h4>{selectedItem.title}</h4>
                <p>Category: {selectedItem.category}</p>
                <p>Price: ${selectedItem.price}</p>
              </div>
              <div>
                <IconButton onClick={() => handleQuantityChange(-1)}>
                  <Remove />
                </IconButton>
                <span>{selectedItem.quantity}</span>
                <IconButton onClick={() => handleQuantityChange(1)}>
                  <Add />
                </IconButton>
                <IconButton onClick={handleDelete}>
                  <Delete />
                </IconButton>
              </div>
            </CardContent>
          </Card>
        ) : (
          <p>No product selected.</p>
        )}
      </Grid>
    </Grid>
  );
};
