import React, { useState, useEffect } from "react";
import "./category.css";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import WestIcon from "@mui/icons-material/West";
import { useNavigate } from "react-router-dom";
import { Button, TextField } from "@mui/material";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import Box from "@mui/material/Box";

export const Addcategory = () => {
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const [data, setData] = useState([]);

  const [fileName, setFileName] = useState("");

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setFileName(file.name);
      console.log("Selected file:", file.name);
      // You can do something with the file here, like uploading it or previewing it
    }
  };

  const navigate = useNavigate();
  const handleback = () => {
    navigate("/login");
  };

  return (
    <div>
      <div
        onClick={handleback}
        style={{
          position: "relative",
          left: "70px",
          top: "40px",
          color: "#3333ff",
          cursor: "pointer",
        }}
      >
        <WestIcon style={{ fontSize: "30px" }} />
      </div>
      <div>
        <h1>Category page</h1>
      </div>
      <div style={{ position: "relative", left: "1200px", top: "-90px" }}>
        <Button color="success" variant="contained" onClick={handleOpen}>
          + Add Category
        </Button>
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box sx={style}>
            <h3>Add Category:-</h3>
            <TextField
              variant="outlined"
              label="Enter category Name"
              fullWidth
            />
            <h3>Add Category Image:-</h3>
            <input
              type="file"
              id="upload"
              style={{ display: "none" }}
              onChange={handleFileChange}
            />
            <Button
              variant="contained"
              color="success"
              onClick={() => document.getElementById("upload").click()}
              style={{
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
                marginRight: "10px",
              }}
            >
              Choose File
            </Button>
            {fileName && <span>{fileName}</span>} <br /> <br />
            <Button variant="contained" fullWidth>
              Submit
            </Button>
          </Box>
        </Modal>
      </div>
      <table>
        <thead>
          <tr>
            <th>SL</th>
            <th>Category Name</th>
            <th>Category Image</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            {}
            <td>1</td>
            <td>Chicken Biryani</td>
            <td>Image Icon</td>
            <td>
              <EditIcon style={{ color: "green", marginRight: "30px" }} />
              <DeleteIcon style={{ color: "red" }} />
            </td>
          </tr>
          <tr>
            <td>2</td>
            <td>Paneer Pulao</td>
            <td>Image Icon</td>
            <td>
              <EditIcon style={{ color: "green", marginRight: "30px" }} />
              <DeleteIcon style={{ color: "red" }} />
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};
