import React from "react";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";
import { Button, Grid } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import MenuBookIcon from "@mui/icons-material/MenuBook";
import RestartAltIcon from "@mui/icons-material/RestartAlt";
import { Showproduct } from "./Showproduct";
import { Itemdetails } from "./Itemdetails";

export const Rightnav = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const item = location.state?.item;

  if (!item) {
    return (
      <div>
        <p>No item data found.</p>
        <Button onClick={() => navigate("/")}>Go Back</Button>
      </div>
    );
  }

  return (
    <Grid container spacing={2}>
      <Grid item xs={1.5}></Grid>
      <Grid item xs={2}>
        <div style={{ height: 37, width: 139, border: "1px solid black" }}>
          <AddIcon style={{ position: "relative", top: 5 }} /> Add Customer
        </div>
      </Grid>
      <Grid item xs={4}></Grid>
      <Grid item xs={1.5}>
        <AddIcon />
      </Grid>
      <Grid item xs={1.5}>
        <MenuBookIcon />
      </Grid>
      <Grid item xs={1.5}>
        <RestartAltIcon />
      </Grid>{" "}
      {/* <Itemdetails /> */}
      <Grid item xs={4}>
        <Card>
          <CardContent>
            <img src={item.image} alt={item.name} height={100} width={100} />
            <Typography variant="h5">{item.name}</Typography>
            <Typography variant="body1">Price: ${item.price}</Typography>
          </CardContent>
        </Card>
      </Grid>
      {/* <Showproduct /> */}
      {/* <Itemdetails /> */}
      {/* <br /> <br /> <br /> <br />
      <br /> <br /> <br /> <br />
      <br /> <br /> <br /> <br />
      <br /> <br /> <br /> <br />
      <br /> <br /> <br /> <br />
      <br /> <br /> <br /> <br /> */}
      <Grid item xs={2}></Grid>
      <Grid item xs={4.5}>
        <Button
          style={{ height: 45, position: "relative", top: 533 }}
          variant="contained"
          fullWidth
        >
          Hold Order
        </Button>
      </Grid>
      <Grid item xs={4.5}>
        <Button
          style={{ height: 45, position: "relative", top: 533 }}
          variant="contained"
          color="success"
          fullWidth
        >
          Proceed
        </Button>
      </Grid>
    </Grid>
  );
};
