import React, { useState } from "react";
import { Box, Tabs, Tab } from "@mui/material";
import { Grid } from "@mui/material";

export const Nav = () => {
  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={0.5}></Grid>
      <Grid item xs={4.8}>
        <h2>
          <span style={{ color: "#2db300" }}>Medi</span>{" "}
          <span style={{ color: "#4287f5" }}>Pharma</span>
        </h2>
      </Grid>
      <Grid item xs={6.2}>
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="basic tabs example"
          sx={{ justifyContent: "flex-end", display: "flex" }} // Added styles here
        >
          <Tab label="POS" sx={{ color: "green" }} />
          <Tab label="+Add Products" sx={{ color: "green" }} />
          <Tab label="+Add Category" sx={{ color: "green" }} />
          <Tab label="Settings" sx={{ color: "green" }} />
          <Tab label="Reports " sx={{ color: "green" }} />
        </Tabs>
      </Grid>
    </Grid>
  );
};
