import React, { useState } from "react";
import { Link } from "react-router-dom";
import { CardContent, Grid, Card, TextField, Button } from "@mui/material";
import { Radio, Radioo } from "./Radioo";
import { useNavigate } from "react-router-dom";
import { Categorylist } from "./Categorylist";
import { useDispatch } from "react-redux";

export const Addcategory = () => {
  const navigate = useNavigate();
  // const dispatch = useDispatch();

  const [name, setName] = useState("");
  const [data, setData] = useState([]);
  const [radio, setRadio] = useState("");

  const handleCli = () => {
    setData([...data, name]);

    // navigate("/category", { state: { name, radio } });
    navigate("/category");
  };
  // const type = "ADD-DATA";
  // const payload = { name };
  // const action = { type, payload };
  // dispatch(action);

  return (
    <Card sx={{ backgroundColor: "lightsteelblue" }}>
      <CardContent>
        <Grid container spacing={2}>
          <Grid item xs={3}>
            <h2>Add Category</h2>
          </Grid>

          <Grid item xs={9}></Grid>
          <Grid item xs={12}>
            <Grid container spacing={2}>
              <Grid item xs={6}></Grid>
              <Grid item xs={2}>
                <h3>
                  Category Name<span style={{ color: "red" }}>*</span>
                </h3>
              </Grid>
              <Grid item xs={3}>
                <TextField
                  setName={setName}
                  name={name}
                  onChange={(e) => setName(e.target.value)}
                  variant="outlined"
                  fullWidth
                  label="Category Name"
                />
              </Grid>
              <Grid item xs={12}>
                <Grid container spacing={2}>
                  <Grid item xs={6}></Grid>
                  <Grid item xs={2}>
                    <h2>
                      Status <span style={{ color: "red" }}>*</span>
                    </h2>
                  </Grid>
                  <Grid item xs={1.4}>
                    <Radioo />
                  </Grid>
                  <Grid item xs={3}></Grid>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
          <Grid item xs={10}></Grid>
          <Grid item xs={2}>
            {/* <Link to="category"> */}
            <Button
              onClick={handleCli}
              variant="contained"
              color="success"
              fullWidth
            >
              {" "}
              Save
            </Button>
            {/* </Link> */}
          </Grid>
        </Grid>
        <Categorylist name={name} setName={setName} />
      </CardContent>
    </Card>
  );
};
