import { Button } from "@mui/material";
import React, { useState } from "react";

export const Sam = () => {
  const [on, setOn] = useState(false);

  const hanldeClick = () => {
    setOn(!on);
  };

  return (
    <div>
      <Button onClick={hanldeClick} variant="contained" fullWidth>
        {on ? "ONNN" : "OFFFF"}
      </Button>
      {on || <h1>Data is shown because the switch is ON</h1>}
    </div>
  );
};
