// Smallnav.js
import React from "react";
import { useNavigate } from "react-router-dom";
import { Card, CardContent, Grid } from "@mui/material";

const items = [
  {
    name: "Schewan Egg Noodles",
    price: 25,
    image:
      "https://media.istockphoto.com/id/2063029282/photo/gobi-manchurian-is-a-crispy-fried-cauliflower-made-with-chiles-garlic-and-ginger-sauce.webp?b=1&s=170667a&w=0&k=20&c=axdmGdDzIR3AOUKC95DLwjocQ_2KdjLheinESYG0Bo4=",
  },
  // Add more items here
];

export const Smallnav = () => {
  // const navigate = useNavigate();

  // const handleClick = (item) => {
  //   navigate("/item-detail", { state: { item } });
  // };

  return (
    <div>
      <Grid container spacing={2}>
        <Grid item xs={2}>
          <b>Starters</b>
        </Grid>
        <Grid item xs={2}>
          <b>Breakfast</b>
        </Grid>
        <Grid item xs={2}>
          <b>Lunch</b>
        </Grid>
        <Grid item xs={2}>
          <b>Super</b>
        </Grid>
        <Grid item xs={2}>
          <b>Desserts</b>
        </Grid>
        <Grid item xs={2}>
          <b>Beverage</b>
        </Grid>
        {items.map((item, index) => (
          <Grid item xs={3} key={index}>
            <Card style={{ height: 218 }}>
              {/* onClick={() => handleClick(item)} */}
              <CardContent>
                <img
                  src={item.image}
                  alt={item.name}
                  height={100}
                  width={100}
                />
                <p>{item.name}</p>
                <p>${item.price}</p>
              </CardContent>
            </Card>
