import React, { useState } from "react";
import { Button, Grid, TextField } from "@mui/material";

export const Sam = () => {
  const [name, setName] = useState("");
  const [txt, setText] = useState("");
  const [data, setData] = useState([]);

  const handleAdd = () => {
    setData([...data, { txt, name }]);
    setName("");
  };

  const handleDel = (index) => {
    const filt = data.filter((elem, i) => i !== index);
    setData(filt);
  };
  return (
    <div>
      <Grid container spacing={2}>
        <Grid item xs={3}>
          <TextField
            value={name}
            onChange={(e) => setName(e.target.value)}
            variant="outlined"
            fullWidth
          />
        </Grid>
        <Grid item xs={3}>
          <TextField
            value={txt}
            onChange={(e) => setText(e.target.value)}
            variant="outlined"
            fullWidth
          />
        </Grid>
        <Grid item x={4}>
          <Button onClick={handleAdd} variant="contained" fullWidth>
            Add
          </Button>
        </Grid>
        {data.map((item, index) => {
          return (
            <div>
              <span>{item.name}</span>
              <span>{item.txt}</span>
              <h2>{index}</h2>
              <Button onClick={() => handleDel(index)}>DELELTE</Button>
            </div>
          );
        })}
      </Grid>
    </div>
  );
};
