import React, { useState } from "react";
import { Grid, TextField, Button, Card, CardContent } from "@mui/material";
import ToggleOnIcon from "@mui/icons-material/ToggleOn";
import ToggleOffIcon from "@mui/icons-material/ToggleOff";

import "./common.css";
// import backgroundImage from "../assets/your-image.jpg";

export const Home = () => {
  const [isToggled, setIsToggled] = useState(false);

  const handleToggle = () => {
    setIsToggled(!isToggled);
  };
  return (
    <Grid container className="login-container">
      <Grid item xs={12}>
        <img src="syringe.jpg" alt="" className="photo" />
      </Grid>
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        className="form-container"
      >
        <Grid item xs={12} sm={6} md={4} lg={3.5}>
          <Card>
            <CardContent>
              <h1>
                Medi <span style={{ color: "green" }}>Pharma</span>
              </h1>
              <p style={{ marginLeft: "20px" }}>Login To Continue</p>
              <form>
                <TextField
                  label="Username"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                />
                <TextField
                  label="Password"
                  type="password"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                />

                <div
                  onClick={handleToggle}
                  style={{
                    display: "flex",
                    // justifyContent: "center",
                    // alignItems: "center",
                    margin: "20px 0",
                  }}
                >
                  {isToggled ? (
                    <ToggleOnIcon style={{ fontSize: 50, color: "#538eed" }} />
                  ) : (
                    <ToggleOffIcon style={{ fontSize: 50, color: "gray" }} />
                  )}
                  <span
                    style={{
                      position: "relative",
                      top: "-2px",
                      right: "-15px",
                    }}
                  >
                    <p> Remember Me</p>
                  </span>
                </div>

                <Button variant="contained" color="success" fullWidth>
                  Login
                </Button>
              </form>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Grid>
  );
};
