import React, { useEffect, useState } from "react";
import "./update.css";
import { Grid, Card, CardContent, TextField } from "@mui/material";
import Button from "@mui/material/Button";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";
import { useParams } from "react-router-dom";
import axios from "axios";

export const Update = () => {
  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  const { categoryId } = useParams(); // Correctly call useParams
  const [categoryName, setCategoryName] = useState("");
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState("");

  useEffect(() => {
    if (categoryId) {
      axios
        .get(`http://localhost:8090/api/category/${categoryId}`)
        .then((res) => {
          console.log("Fetched data:", res.data);
          //   setCategoryName(res.data.categoryName || "");
        })
        .catch((err) => {
          console.error(
            "Error fetching category:",
            err.response ? err.response.data : err.message
          );
        });
    }
  }, [categoryId]);

  //   const handleFileChange = (event) => {
  //     const selectedFile = event.target.files[0];
  //     if (selectedFile) {
  //       setFile(selectedFile);
  //       setFileName(selectedFile.name);
  //     }
  //   };

  //   const handleSubmit = () => {
  //     const formData = new FormData();
  //     formData.append("categoryName", categoryName);
  //     if (file) {
  //       formData.append("file", file);
  //     }

  //     // Log FormData for debugging
  //     for (let pair of formData.entries()) {
  //       console.log(pair[0], pair[1]);
  //     }

  //     axios
  //       .put(
  //         `http://localhost:8090/api/category/update/${categoryId}`,
  //         formData,
  //         {
  //           headers: {
  //             "Content-Type": "multipart/form-data",
  //           },
  //         }
  //       )
  //       .then((response) => {
  //         console.log("Update response:", response.data);
  //         alert("Category updated successfully.");
  //       })
  //       .catch((error) => {
  //         console.error(
  //           "Error updating category:",
  //           error.response ? error.response.data : error.message
  //         );
  //       });
  //   };

  return (
    <Grid
      container
      spacing={2}
      justifyContent="center"
      alignItems="center"
      style={{ minHeight: "100vh" }}
    >
      <Grid item xs={12} md={6}>
        <Card>
          <CardContent>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  fullWidth
                  label="Enter Category Name"
                  value={categoryName}
                  onChange={(e) => setCategoryName(e.target.value)}
                />
              </Grid>
              <Grid item xs={12}>
                <Button
                  component="label"
                  role={undefined}
                  variant="contained"
                  startIcon={<CloudUploadIcon />}
                >
                  Upload file
                  <VisuallyHiddenInput
                    type="file"
                    // onChange={handleFileChange}
                  />
                </Button>
                {fileName && <span style={{ marginLeft: 10 }}>{fileName}</span>}
              </Grid>
              <Grid item xs={12}>
                <Button
                  fullWidth
                  variant="contained"
                  color="success"
                  //   onClick={handleSubmit}
                >
                  Submit
                </Button>
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
};
