import React from "react";
import { Grid, TextField, Button, Card, CardContent } from "@mui/material";
import "./common.css";
import ToggleOnIcon from "@mui/icons-material/ToggleOn";

// import backgroundImage from "../assets/your-image.jpg";

export const Home = () => {
  <div
    onClick={handleToggle}
    style={{
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      margin: "20px 0",
    }}
  >
    {isToggled ? (
      <ToggleOnIcon style={{ fontSize: 40, color: "green" }} />
    ) : (
      <ToggleOffIcon style={{ fontSize: 40, color: "gray" }} />
    )}
  </div>;
  return (
    <Grid container className="login-container">
      <Grid item xs={12}>
        <img src="syringe.jpg" alt="" className="photo" />
      </Grid>
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        className="form-container"
      >
        <Grid item xs={12} sm={6} md={4} lg={3.5}>
          <Card>
            <CardContent>
              <h1>
                Medi <span style={{ color: "green" }}>Pharma</span>
              </h1>
              <p style={{ marginLeft: "20px" }}>Login To Continue</p>
              <form>
                <TextField
                  label="Username"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                />
                <TextField
                  label="Password"
                  type="password"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                />

                <div
                  onClick={handleToggle}
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    margin: "20px 0",
                  }}
                >
                  {isToggled ? (
                    <ToggleOnIcon style={{ fontSize: 40, color: "green" }} />
                  ) : (
                    <ToggleOffIcon style={{ fontSize: 40, color: "gray" }} />
                  )}
                </div>

                <Button variant="contained" color="success" fullWidth>
                  Login
                </Button>
              </form>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Grid>
  );
};
