import React, { useState, useEffect } from "react";
import {
  Box,
  Button,
  Modal,
  Typography,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import axios from "axios";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";

export const Nav = () => {
  const [openAddProduct, setOpenAddProduct] = useState(false);
  const [data, setData] = useState([]);
  const [productName, setProductName] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [price, setPrice] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileName, setFileName] = useState("");

  useEffect(() => {
    const getData = async () => {
      const result = await axios.get(
        "http://localhost:8090/api/category/getAllCategory"
      );
      setData(result.data);
    };
    getData();
  }, []);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setFileName(file ? file.name : "");
  };

  const handleAddProduct = async () => {
    if (!selectedFile) {
      alert("Please select a file first.");
      return;
    }

    const product = {
      productName: productName,
      productPrice: price,
    };

    const formData = new FormData();
    formData.append(
      "product",
      new Blob([JSON.stringify(product)], { type: "application/json" })
    );
    formData.append("file", selectedFile);

    try {
      const response = await axios.post(
        `http://localhost:8090/api/product/add-product/${selectedCategory}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Accept: "application/json",
          },
        }
      );

      console.log("Response:", response.data);
      alert("Product is added successfully!");

      // Reset form values to empty
      setProductName("");
      setSelectedCategory("");
      setPrice("");
      setFileName("");
      setSelectedFile(null);
      setOpenAddProduct(false); // Close modal on success
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to add product. Please try again.");
    }
  };

  const AddProductModal = ({ open, onClose }) => (
    <Modal
      open={open}
      onClose={onClose}
      aria-labelledby="add-product-modal"
      aria-describedby="add-product-description"
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Box
        sx={{
          width: "80%",
          maxWidth: 600,
          bgcolor: "background.paper",
          borderRadius: 2,
          boxShadow: 24,
          p: 4,
          backdropFilter: "blur(10px)",
        }}
      >
        <Typography variant="h6" component="h2">
          Add New Product
        </Typography>
        <TextField
          label="Product Name"
          fullWidth
          sx={{ mb: 2 }}
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel id="select-label">Select Category</InputLabel>
          <Select
            labelId="select-label"
            label="Select Category"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            {data.map((item) => (
              <MenuItem key={item.id} value={item.categoryName}>
                {item.categoryName}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        <TextField
          label="Price"
          type="number"
          fullWidth
          sx={{ mb: 2 }}
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <div>
          <Button
            component="label"
            variant="contained"
            startIcon={<CloudUploadIcon />}
          >
            Upload Product Image
            <input
              type="file"
              style={{ display: "none" }}
              onChange={handleFileChange}
            />
          </Button>
          {fileName && <p>Selected File: {fileName}</p>}
        </div>
        <br />
        <Button
          variant="contained"
          color="primary"
          sx={{ mr: 2 }}
          onClick={handleAddProduct}
        >
          Add Product
        </Button>
        <Button variant="outlined" color="secondary" onClick={onClose}>
          Cancel
        </Button>
      </Box>
    </Modal>
  );

  return (
    <div>
      <Button onClick={() => setOpenAddProduct(true)}>+ Add Product</Button>

      {/* Add Product Modal */}
      <AddProductModal
        open={openAddProduct}
        onClose={() => setOpenAddProduct(false)}
      />
    </div>
  );
};
