import React, { useState, useEffect } from "react";
import { Button, Grid, TextField, Typography } from "@mui/material";
import WifiPasswordIcon from "@mui/icons-material/WifiPassword";
import RestartAltIcon from "@mui/icons-material/RestartAlt";
import { Leftbar } from "./Leftbar";
import HomeIcon from "@mui/icons-material/Home";
import PersonOutlineIcon from "@mui/icons-material/PersonOutline";
import DashboardIcon from "@mui/icons-material/Dashboard";
import CurrencyExchangeIcon from "@mui/icons-material/CurrencyExchange";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import ReportIcon from "@mui/icons-material/Report";
import SettingsIcon from "@mui/icons-material/Settings";
import LogoutIcon from "@mui/icons-material/Logout";
import { Smallnav } from "./Smallnav";
import { Rightnav } from "./Rightnav";
import { Itemdetails } from "./Itemdetails";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";
import { Card, CardContent } from "@mui/material";

// import ReportIcon from "@mui/icons-material/Report";
// import SettingsIcon from "@mui/icons-material/Settings";
// import LogoutIcon from "@mui/icons-material/Logout";
import RemoveIcon from "@mui/icons-material/Remove";
import IconButton from "@mui/icons-material/IconButton";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";

export const Home = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const { item } = location.state || {};

  // const location = useLocation();
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    if (location.state && location.state.item) {
      setSelectedItems((prevItems) => [
        ...prevItems,
        { ...location.state.item, quantity: 1 },
      ]);
    }
  }, [location.state]);

  const handleIncreaseQuantity = (index) => {
    const newItems = [...selectedItems];
    newItems[index].quantity += 1;
    setSelectedItems(newItems);
  };

  const handleDecreaseQuantity = (index) => {
    const newItems = [...selectedItems];
    if (newItems[index].quantity > 1) {
      newItems[index].quantity -= 1;
      setSelectedItems(newItems);
    }
  };

  const totalPrice = selectedItems.reduce(
    (total, item) => total + item.price * item.quantity,
    0
  );
  return (
    <Grid container spacing={2}>
      <Grid item xs={2.5}>
        <h2>RESTO POST</h2>
      </Grid>
      <Grid item xs={3.5}>
        <TextField
          variant="outlined"
          label="Search Products...."
          fullWidth
          style={{ marginTop: 10 }}
        />
      </Grid>
      <Grid item xs={3}></Grid>
      <Grid item xs={0.5} style={{ marginTop: 15 }}>
        <RestartAltIcon />
      </Grid>
      <Grid item xs={0.5} style={{ marginTop: 15 }}>
        <WifiPasswordIcon />
      </Grid>
      <Grid item xs={1.5} style={{ marginTop: 15 }}>
        <Button
          variant="contained"
          fullWidth
          style={{ backgroundColor: "#ff3d00" }}
        >
          Select Table
        </Button>
      </Grid>
      <Grid item xs={0.5}></Grid>
      <Grid
        item
        xs={1.5}
        style={{
          height: 670,
          //   border: "2px solid black",
          marginTop: 20,
        }}
      >
        <HomeIcon style={{ height: 20 }} /> <br />{" "}
        <Typography variant="h9" style={{ fontSize: 12 }}>
          Home
        </Typography>{" "}
        <br />
        <br />
        <PersonOutlineIcon style={{ height: 20 }} /> <br />{" "}
        <Typography variant="h9" style={{ fontSize: 12 }}>
          Customers
        </Typography>{" "}
        <br /> <br />
        <DashboardIcon style={{ height: 20 }} /> <br />{" "}
        <Typography variant="h9" style={{ fontSize: 12 }}>
          Dashboard
        </Typography>{" "}
        <br /> <br />
        <CurrencyExchangeIcon style={{ height: 20 }} /> <br />{" "}
        <Typography variant="h9" style={{ fontSize: 12 }}>
          Casheair
        </Typography>{" "}
        <br /> <br />
        <ShoppingCartIcon style={{ height: 20 }} /> <br />{" "}
        <Typography variant="h9" style={{ fontSize: 12 }}>
          Orders
        </Typography>{" "}
        <br /> <br />
        <ReportIcon style={{ height: 20 }} /> <br />{" "}
        <Typography variant="h9" style={{ fontSize: 12 }}>
          Reports
        </Typography>{" "}
        <br /> <br />
        <SettingsIcon style={{ height: 20 }} /> <br />{" "}
        <Typography variant="h9" style={{ fontSize: 12 }}>
          Settings
        </Typography>{" "}
        <br /> <br /> <br /> <br /> <br /> <br /> <br />
        <LogoutIcon /> <br />
        <Typography variant="h9" style={{ fontSize: 12 }}>
          Logout
        </Typography>{" "}
      </Grid>
      <Grid item xs={6}>
        <Smallnav />
      </Grid>
      <Grid item xs={4} style={{ border: "0.5px solid black" }}>
        <Rightnav />
        <Grid item={6}>
          <div>
            <h3> !!Your Order Items!!</h3>
            <Grid container spacing={2}>
              {selectedItems.map((item, index) => (
                <Grid item xs={4} key={index}>
                  <Card style={{ height: 218 }}>
                    <CardContent>
                      <img
                        src={item.image}
                        alt={item.name}
                        height={100}
                        width={100}
                      />
                      <p>{item.name}</p>
                      <p>${item.price}/-</p>
                      <Grid container spacing={1}>
                        <Grid item>
                          <IconButton
                            onClick={() => handleDecreaseQuantity(index)}
                          >
                            <RemoveIcon />
                          </IconButton>
                        </Grid>
                        <Grid item>
                          <Typography>{item.quantity}</Typography>
                        </Grid>
                        <Grid item>
                          <IconButton
                            onClick={() => handleIncreaseQuantity(index)}
                          >
                            <AddIcon />
                          </IconButton>
                        </Grid>
                      </Grid>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </div>
          <h3>Total Price: ${totalPrice}</h3>
        </Grid>
      </Grid>
    </Grid>
  );
};
