import React, { useState } from "react";
import { CardContent, Grid, Card, TextField, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { Radioo } from "./Radioo";

export const AddCategory = ({ onAddCategory }) => {
  const [name, setName] = useState("");
  const [radio, setRadio] = useState("");

  const handleCli = () => {
    onAddCategory({ name, radio });
    setName("");
  };

  return (
    <Card sx={{ backgroundColor: "lightsteelblue" }}>
      <CardContent>
        <Grid container spacing={2}>
          <Grid item xs={3}>
            <h2>Add Category</h2>
          </Grid>

          <Grid item xs={9}></Grid>
          <Grid item xs={12}>
            <Grid container spacing={2}>
              <Grid item xs={6}></Grid>
              <Grid item xs={2}>
                <h3>
                  Category Name<span style={{ color: "red" }}>*</span>
                </h3>
              </Grid>
              <Grid item xs={3}>
                <TextField
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  variant="outlined"
                  fullWidth
                  label="Category Name"
                />
              </Grid>
              <Grid item xs={12}>
                <Grid container spacing={2}>
                  <Grid item xs={6}></Grid>
                  <Grid item xs={2}>
                    <h2>
                      Status <span style={{ color: "red" }}>*</span>
                    </h2>
                  </Grid>
                  <Grid item xs={1.4}>
                    <Radioo
                      value={radio}
                      onChange={(e) => setRadio(e.target.value)}
                    />
                  </Grid>
                  <Grid item xs={3}></Grid>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
          <Grid item xs={10}></Grid>
          <Grid item xs={2}>
            <Button
              onClick={handleCli}
              variant="contained"
              color="success"
              fullWidth
            >
              Save
            </Button>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};
