import React from "react";
import "./update.css";
import { Grid, Card, CardContent, TextField } from "@mui/material";
import Button from "@mui/material/Button";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";

export const Update = () => {
  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });
  return (
    <Grid container spacing={2}>
      <Grid item xs={4}></Grid>
      <Grid sx={{ marginTop: "200px" }} item xs={4}>
        <Grid container spacing={2}>
          <Grid item xs={4}>
            <h4>Category Name:-</h4>
          </Grid>
          <Grid item xs={8}>
            <TextField
              variant="outlined"
              fullWidth
              label="Enter Category Name"
            />
          </Grid>
          <Grid item xs={4}></Grid>
          <Card>
            <CardContent>
              <Grid item xs={8}>
                <Button
                  component="label"
                  role={undefined}
                  variant="contained"
                  tabIndex={-1}
                  startIcon={<CloudUploadIcon />}
                >
                  Upload file
                  <VisuallyHiddenInput type="file" />
                </Button>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      <Grid item xs={4}></Grid>
    </Grid>
  );
};
