import React, { useState } from "react";
import { Button, Grid } from "@mui/material";

export const Home = () => {
  const [data, setData] = useState(0);

  const handleClick = () => {
    setData(data + 1);
    if (data === 10) {
      alert("You ARE cROSSING tHE lIMIT");
    }
  };

  const handleReset = () => {
    setData(0);
  };
  return (
    <Grid container spacing={2}>
      <Grid item xs={12}>
        <h1>Counter</h1>
      </Grid>
      <Grid item xs={4}>
        <Button
          sx={{ height: 55 }}
          onClick={handleClick}
          variant="contained"
          color="success"
          fullWidth
        >
          Increment
        </Button>
      </Grid>
      <Grid item xs={4}>
        <h1>{data}</h1>
      </Grid>
      <Grid item xs={4}>
        <Button
          sx={{ height: 55 }}
          onClick={() => setData(data - 1)}
          variant="contained"
          fullWidth
        >
          Decrement
        </Button>
      </Grid>
      <Grid item xs={4}></Grid>
      <Grid item xs={4}>
        <Button
          onClick={handleReset}
          variant="contained"
          color="error"
          fullWidth
        >
          Reset
        </Button>
      </Grid>
      <Grid item xs={4}></Grid>
    </Grid>
  );
};
