import React, { useEffect, useState } from "react";
import { Button, Card, CardContent, Grid, TextField } from "@mui/material";
import { Link, useNavigate } from "react-router-dom";

export const Form = () => {
  const [email, setEmail] = useState("");
  const [pass, setPass] = useState("");
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleClick = () => {
    localStorage.setItem("email", email);
    localStorage.setItem("pass", pass);

    const emails = localStorage.getItem("email");
    const passwords = localStorage.getItem("pass");

    if (emails === "arbaz@gmail.com" && passwords === "12345") {
      navigate("cat");
    } else {
      navigate("/");
      // alert("Plz Type The Correct Pasword !!!!");
      setError("Please Check THe Password");
    }
  };

  useEffect(() => {
    setTimeout(() => {
      setError("");
    }, 2000);
  }, [error]);

  return (
    <Grid container spacing={2}>
      <Grid item xs={4}></Grid>

      <Grid item xs={4}>
        <Card>
          <CardContent>
            <h3>Sign In </h3>
            <p
              style={{ position: "relative", left: "-135px", fontWeight: 600 }}
            >
              Your E-mail
            </p>
            <TextField
              onChange={(e) => setEmail(e.target.value)}
              variant="outlined"
              fullWidth
              label="Email@example.com"
            />
            <p style={{ position: "relative", left: "-80px", fontWeight: 600 }}>
              Password{" "}
              <span
                style={{
                  position: "relative",
                  left: "160px",
                  color: "lightblue",
                }}
              >
                Forget Password?
              </span>
            </p>
            <TextField
              onChange={(e) => setPass(e.target.value)}
              //   style={{ position: "relative", top: "80" }}
              variant="outlined"
              fullWidth
              label="Password"
              type="password"
            />
            <span>{error}</span>
            <br /> <br />
            {/* <Link to="cat"> */}
            <Button
              onClick={handleClick}
              variant="contained"
              color="success"
              fullWidth
            >
              Submit
            </Button>
            {/* </Link> */}
          </CardContent>
        </Card>
      </Grid>
      <Grid item xs={4}></Grid>
    </Grid>
  );
};
