import React, { useEffect, useState } from "react";
import {
  CardContent,
  Card,
  Grid,
  TextField,
  InputAdornment,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  Box,
  Button,
  Select,
  MenuItem,
} from "@mui/material";
import { Search, Add, Remove, Delete } from "@mui/icons-material";
import axios from "axios";

export const Fetching = () => {
  const [data, setData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedItems, setSelectedItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("All");

  // Fetch data from the API
  const getData = async () => {
    try {
      const result = await axios.get("https://fakestoreapi.com/products");
      setData(result.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  // Handle change in search input
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Handle change in category
  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  // Handle product click
  const handleProductClick = (item) => {
    setSelectedItems((prevItems) => {
      const existingItem = prevItems.find((i) => i.id === item.id);
      if (existingItem) {
        return prevItems.map((i) =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        );
      } else {
        return [...prevItems, { ...item, quantity: 1 }];
      }
    });
  };

  // Handle quantity change
  const handleQuantityChange = (itemId, change) => {
    setSelectedItems((prevItems) =>
      prevItems.map((item) =>
        item.id === itemId
          ? { ...item, quantity: Math.max(item.quantity + change, 1) }
          : item
      )
    );
  };

  // Handle delete
  const handleDelete = (itemId) => {
    setSelectedItems((prevItems) =>
      prevItems.filter((item) => item.id !== itemId)
    );
  };

  // Handle place order
  const handlePlaceOrder = () => {
    alert("Order placed successfully!");
    setSelectedItems([]);
  };

  // Handle cancel order
  const handleCancelOrder = () => {
    setSelectedItems([]);
  };

  // Get unique categories
  const categories = ["All", ...new Set(data.map((item) => item.category))];

  // Filter data based on search query and selected category
  const filteredData = data.filter(
    (item) =>
      item.category.toLowerCase().includes(searchQuery.toLowerCase()) &&
      (selectedCategory === "All" || item.category === selectedCategory)
  );

  // Calculate totals
  const total = selectedItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );
  const tax = total * 0.1; // 10% tax
  const discount = total * 0.05; // 5% discount
  const grossTotal = total + tax - discount;

  return (
    <Grid container spacing={2}>
      <Grid item xs={6}>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Search by category"
              variant="outlined"
              value={searchQuery}
              onChange={handleSearchChange}
              sx={{
                marginBottom: "20px",
                width: "100%",
                "& .MuiOutlinedInput-root": {
                  borderRadius: "20px",
                },
              }}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>
          <Grid item xs={6}>
            <Select
              label="Category"
              value={selectedCategory}
              onChange={handleCategoryChange}
              sx={{
                marginBottom: "20px",
                width: "100%",
                "& .MuiSelect-root": {
                  borderRadius: "20px",
                },
                "& fieldset": {
                  borderRadius: "20px", // Ensures the outline of the Select component is also rounded
                },
              }}
            >
              {categories.map((category) => (
                <MenuItem key={category} value={category}>
                  {category}
                </MenuItem>
              ))}
            </Select>
          </Grid>
        </Grid>

        <Box
          sx={{
            maxHeight: "400px",
            overflowY: "auto",
            paddingRight: "10px", // To prevent scroll bar overlap
          }}
        >
          <Grid container spacing={2}>
            {filteredData.map((item) => (
              <Grid item xs={3} key={item.id}>
                <Card
                  sx={{ height: "200px", backgroundColor: "#f2f2f2" }}
                  onClick={() => handleProductClick(item)}
                >
                  <CardContent>
                    <img
                      src={item.image}
                      alt={item.title}
                      height={100}
                      width={100}
                    />
                    <h5>{item.category.toUpperCase()}</h5>
                    <center>
                      <b style={{ position: "relative", top: "-20px" }}>
                        ${item.price}/-
                      </b>
                    </center>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Grid>

      {/* Selected Items Display Grid */}
      <Grid item xs={6}>
        <h3>Selected Products</h3>
        <Box
          sx={{
            maxHeight: "300px",
            overflowY: "auto",
            paddingRight: "10px", // To prevent scroll bar overlap
          }}
        >
          {selectedItems.length > 0 ? (
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell
                      sx={{ backgroundColor: "#3385ff", color: "#fff" }}
                    >
                      Product Name
                    </TableCell>
                    <TableCell
                      sx={{ backgroundColor: "#3385ff", color: "#fff" }}
                    >
                      Price
                    </TableCell>
                    <TableCell
                      sx={{ backgroundColor: "#3385ff", color: "#fff" }}
                    >
                      Quantity
                    </TableCell>
                    <TableCell
                      sx={{ backgroundColor: "#3385ff", color: "#fff" }}
                    >
                      Actions
                    </TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {selectedItems.map((item) => (
                    <TableRow
                      key={item.id}
                      sx={{
                        transition: "background-color 0.3s",
                        "&:hover": { backgroundColor: "#f5f5f5" },
                      }}
                    >
                      <TableCell>{item.title.slice(0, 20)}</TableCell>
                      <TableCell>${item.price.toFixed(2)}</TableCell>
                      <TableCell>
                        <IconButton
                          onClick={() => handleQuantityChange(item.id, -1)}
                        >
                          <Remove />
                        </IconButton>
                        <span>{item.quantity}</span>
                        <IconButton
                          onClick={() => handleQuantityChange(item.id, 1)}
                        >
                          <Add />
                        </IconButton>
                      </TableCell>
                      <TableCell>
                        <IconButton onClick={() => handleDelete(item.id)}>
                          <Delete style={{ color: "#ff1a1a" }} />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          ) : (
            <p>No products selected.</p>
          )}
        </Box>
        {/* Totals Card */}
        <Card
          sx={{
            marginTop: "20px",
            padding: "10px",
            backgroundColor: "#f8f9fa",
          }}
        >
          <CardContent>
            <Box display="flex" flexDirection="column" alignItems="flex-end">
              <Typography variant="subtitle1">
                Total: ${total.toFixed(2)}
              </Typography>
              <Typography variant="subtitle1">
                Tax (10%): ${tax.toFixed(2)}
              </Typography>
              <Typography variant="subtitle1">
                Discount (5%): -${discount.toFixed(2)}
              </Typography>
              <Typography variant="subtitle1">
                Gross Total: ${grossTotal.toFixed(2)}
              </Typography>
            </Box>
          </CardContent>
        </Card>
        <Box display="flex" justifyContent="center" marginTop="10px">
          <Button
            onClick={handlePlaceOrder}
            variant="contained"
            color="primary"
            sx={{ margin: "5px" }}
          >
            Place Order
          </Button>
          <Button
            onClick={handleCancelOrder}
            variant="contained"
            color="error"
            sx={{ margin: "5px" }}
          >
            Cancel Order
          </Button>
        </Box>
      </Grid>
    </Grid>
  );
};
