import React, { useEffect, useState } from "react";
import {
  Box,
  Tabs,
  Tab,
  Button,
  Modal,
  Typography,
  TextField,
  CardContent,
  Card,
} from "@mui/material";
import { Grid } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { Select, MenuItem, FormControl, InputLabel } from "@mui/material";
import axios from "axios";
import { styled } from "@mui/material/styles";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";

export const Nav = () => {
  const navigate = useNavigate();
  const [value, setValue] = useState(0);
  const [openAddProduct, setOpenAddProduct] = useState(false);
  const [openAddCategory, setOpenAddCategory] = useState(false);
  const [data, setData] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  console.log("dataaa", data);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleSet = () => {
    navigate("/setting");
  };

  const handleLogout = () => {
    navigate("/");
  };

  const getData = async () => {
    const result = await axios.get(
      "http://localhost:8090/api/category/getAllCategory"
    );
    setData(result.data);
  };

  useEffect(() => {
    getData();
  }, []);

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (!selectedFile) {
      alert("Please select a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      // Adjust the URL according to your API endpoint
      const response = await axios.post(
        "http://localhost:8090/api/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log("File uploaded successfully:", response.data);
      alert("File uploaded successfully!");
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Failed to upload file.");
    }
  };

  const AddProductModal = ({ open, onClose }) => (
    <Modal
      open={open}
      onClose={onClose}
      aria-labelledby="add-product-modal"
      aria-describedby="add-product-description"
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Box
        sx={{
          width: "80%",
          maxWidth: 600,
          bgcolor: "background.paper",
          borderRadius: 2,
          boxShadow: 24,
          p: 4,
          backdropFilter: "blur(10px)",
        }}
      >
        <Typography variant="h6" component="h2">
          Add New Product
        </Typography>
        <TextField label="Product Name" fullWidth sx={{ mb: 2 }} />
        <FormControl fullWidth>
          <InputLabel id="select-label">Select Category</InputLabel>
          <Select
            labelId="select-label"
            label="Select Category"
            defaultValue=""
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            {data.map((item) => (
              <MenuItem key={item.id} value={item.id}>
                {item.categoryName}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        <br /> <br />
        <TextField label="Price" type="number" fullWidth sx={{ mb: 2 }} />
        <Button
          component="label"
          role={undefined}
          variant="contained"
          tabIndex={-1}
          startIcon={<CloudUploadIcon />}
        >
          Upload file
          <VisuallyHiddenInput type="file" onChange={handleFileChange} />
        </Button>
        <br /> <br />
        <Button
          variant="contained"
          color="primary"
          sx={{ mr: 2 }}
          onClick={handleFileUpload}
        >
          Add Product
        </Button>
        <Button variant="outlined" color="secondary" onClick={onClose}>
          Cancel
        </Button>
      </Box>
    </Modal>
  );

  const AddCategoryModal = ({ open, onClose }) => (
    <Modal
      open={open}
      onClose={onClose}
      aria-labelledby="add-category-modal"
      aria-describedby="add-category-description"
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Box
        sx={{
          width: "80%",
          maxWidth: 600,
          bgcolor: "background.paper",
          borderRadius: 2,
          boxShadow: 24,
          p: 4,
          backdropFilter: "blur(10px)",
        }}
      >
        <Typography variant="h6" component="h2">
          Add New Category
        </Typography>
        <TextField label="Category Name" fullWidth sx={{ mb: 2 }} />
        <TextField
          label="Description"
          multiline
          rows={4}
          fullWidth
          sx={{ mb: 2 }}
        />
        <Button variant="contained" color="primary" sx={{ mr: 2 }}>
          Add Category
        </Button>
        <Button variant="outlined" color="secondary" onClick={onClose}>
          Cancel
        </Button>
      </Box>
    </Modal>
  );

  return (
    <Grid container spacing={2}>
      <Grid item xs={0.5}></Grid>
      <Grid item xs={4.5}>
        <h2
          style={{
            marginTop: "10px",
            fontFamily: "cursive",
          }}
        >
          <span style={{ color: "#2db300" }}>Medi</span>{" "}
          <span style={{ color: "#4287f5" }}>Pharma</span>
        </h2>
      </Grid>
      <Grid item xs={7}>
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="basic tabs example"
        >
          <Tab label="POS" sx={{ color: "green" }} />
          <Tab
            label="+Add Products"
            sx={{ color: "green" }}
            onClick={() => setOpenAddProduct(true)}
          />
          <Tab
            label="+Add Category"
            sx={{ color: "green" }}
            onClick={() => setOpenAddCategory(true)}
          />
          <Tab label="Settings" sx={{ color: "green" }} onClick={handleSet} />
          <Tab label="Reports" sx={{ color: "green" }} />
          <Button
            sx={{ borderRadius: "50px", height: "40px" }}
            onClick={handleLogout}
            variant="contained"
            color="error"
          >
            Logout
          </Button>
        </Tabs>
      </Grid>

      {/* Modals */}
      <AddProductModal
        open={openAddProduct}
        onClose={() => setOpenAddProduct(false)}
      />
      <AddCategoryModal
        open={openAddCategory}
        onClose={() => setOpenAddCategory(false)}
      />
    </Grid>
  );
};
