import React from "react";

export const Showproduct = () => {
  return (
    <div>
      <div className="col-md-5  scrollable-table-container mt-3">
        <table className="table mt-3">
          <thead className="fixed">
            <tr>
              <th scope="col">Image</th>
              <th scope="col">Name</th>
              <th scope="col">Price</th>
              <th scope="col">Quantity</th>
              <th scope="col">Total Price</th>
              <th scope="col">Discount</th>
              <th scope="col">Action</th>
            </tr>
          </thead>

          <tbody>
            {selectedProducts.map((product) => (
              <tr key={product.pid}>
                <td>
                  <img
                    src={product.imagepath}
                    alt={product.name}
                    style={{ width: "50px", height: "50px" }}
                  />
                </td>
                <td>
                  {product.name}
                  {/* {product.productDesc} */}
                </td>
                <td>{product.price.toFixed(2)}</td>
                <td>
                  <button
                    onClick={() => incrementQuantity(product.pid)}
                    style={{
                      border: "none",
                      backgroundColor: "white",
                      color: "green",
                    }}
                  >
                    <FontAwesomeIcon icon={faPlus} />
                  </button>
                  {product.quantity}
                  <button
                    onClick={() => decrementQuantity(product.pid)}
                    style={{
                      border: "none",
                      backgroundColor: "white",
                      color: "red",
                    }}
                  >
                    <FontAwesomeIcon icon={faMinus} />
                  </button>
                </td>
                <td>{product.totalPrice.toFixed(2)}</td>
                <td>{(product.discount * product.quantity).toFixed(2)}</td>
                <td>
                  <button
                    onClick={() => removeProduct(product.pid)}
                    style={{
                      border: "none",
                      backgroundColor: "white",
                      color: "red",
                    }}
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};
