import React from "react";
import { Grid, Card, CardContent, IconButton } from "@mui/material";
import { Add, Remove, Delete } from "@mui/icons-material";

export const Billing = ({ selectedItems, onQuantityChange, onDelete }) => {
  return (
    <Grid container spacing={2}>
      <h3>Billing</h3>
      {selectedItems.map((item) => (
        <Grid item xs={12} key={item.id}>
          <Card sx={{ backgroundColor: "#e0e0e0", padding: "10px" }}>
            <CardContent
              sx={{
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
              }}
            >
              <h4>{item.category}</h4>
              <div>
                <IconButton onClick={() => onQuantityChange(item.id, -1)}>
                  <Remove />
                </IconButton>
                <span>{item.quantity}</span>
                <IconButton onClick={() => onQuantityChange(item.id, 1)}>
                  <Add />
                </IconButton>
                <IconButton onClick={() => onDelete(item.id)}>
                  <Delete />
                </IconButton>
              </div>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};
