import React, { useEffect, useState } from "react";
import {
  CardContent,
  Card,
  Grid,
  TextField,
  InputAdornment,
  IconButton,
} from "@mui/material";
import { Search } from "@mui/icons-material";
import { Add, Remove, Delete } from "@mui/icons-material";

import axios from "axios";
import { Selectbar } from "./Selectbar";
import { Bill } from "./Bill";

export const Fetching = () => {
  const [data, setData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedItems, setSelectedItems] = useState([]);

  // Fetch data from the API
  const getData = async () => {
    try {
      const result = await axios.get("https://fakestoreapi.com/products");
      setData(result.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  // Handle change in search input
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Handle image click to add item to the billing section
  const handleImageClick = (item) => {
    setSelectedItems((prevItems) => {
      const existingItem = prevItems.find((i) => i.id === item.id);
      if (existingItem) {
        return prevItems.map((i) =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        );
      } else {
        return [
          ...prevItems,
          { id: item.id, category: item.category, quantity: 1 },
        ];
      }
    });
  };

  // Filter data based on search query
  const filteredData = data.filter((item) =>
    item.category.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleQuantityChange = (itemId, change) => {
    setSelectedItems((prevItems) =>
      prevItems
        .map((item) =>
          item.id === itemId
            ? { ...item, quantity: Math.max(item.quantity + change, 1) }
            : item
        )
        .filter((item) => item.quantity > 0)
    );
  };

  // Handle item delete
  const handleDelete = (itemId) => {
    setSelectedItems((prevItems) =>
      prevItems.filter((item) => item.id !== itemId)
    );
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={6}>
        <TextField
          label="Search by category"
          variant="outlined"
          value={searchQuery}
          onChange={handleSearchChange}
          sx={{
            marginBottom: "20px",
            width: "100%",
            "& .MuiOutlinedInput-root": {
              borderRadius: "20px",
            },
          }}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <Search />
              </InputAdornment>
            ),
          }}
        />
      </Grid>
      <Grid item xs={6}>
        <Selectbar />
      </Grid>
      <Grid item xs={12}>
        <Grid container spacing={2}>
          {filteredData.map((item) => (
            <Grid item xs={3} key={item.id}>
              <Card
                sx={{ height: "200px", backgroundColor: "#f2f2f2" }}
                onClick={() => handleImageClick(item)}
              >
                <CardContent>
                  <img
                    src={item.image}
                    alt={item.title}
                    height={100}
                    width={100}
                  />
                  <h4>{item.category}</h4>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
        {/* <Bill
          selectedItems={selectedItems}
          setSelectedItems={setSelectedItems}
        /> */}
        <Grid item xs={6} style={{ border: "1px solid blue" }}>
          {/* <Bill
            selectedItems={selectedItems}
            setSelectedItems={setSelectedItems}
          /> */}
          <Grid container spacing={2}>
            <h3>Billhhhhing</h3>
            {selectedItems?.map((item) => (
              <Grid item xs={12} key={item.id}>
                <Card sx={{ backgroundColor: "#e0e0e0", padding: "10px" }}>
                  <CardContent
                    sx={{
                      display: "flex",
                      // flexDirection: "row",
                      alignItems: "center",
                      justifyContent: "space-between",
                    }}
                  >
                    <h4>{item.category}</h4>
                    <div>
                      <IconButton
                        onClick={() => handleQuantityChange(item.id, -1)}
                      >
                        <Remove />
                      </IconButton>
                      <span>{item.quantity}</span>
                      <IconButton
                        onClick={() => handleQuantityChange(item.id, 1)}
                      >
                        <Add />
                      </IconButton>
                      <IconButton onClick={() => handleDelete(item.id)}>
                        <Delete />
                      </IconButton>
                    </div>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
};
