import React, { useState } from "react";
import { Grid, TextField, Button, Card, CardContent } from "@mui/material";
import ToggleOnIcon from "@mui/icons-material/ToggleOn";
import ToggleOffIcon from "@mui/icons-material/ToggleOff";
import { useNavigate } from "react-router-dom";

import "./common.css";
// import backgroundImage from "../assets/your-image.jpg";

export const Home = () => {
  const navigate = useNavigate();
  const [isToggled, setIsToggled] = useState(false);

  const handleToggle = () => {
    setIsToggled(!isToggled);
  };

  const handleClick = () => {
    navigate("/login");
  };
  return (
    <Grid container className="login-container">
      <Grid item xs={12}>
        <img src="books.jpg" alt="" className="photo" />
      </Grid>
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        className="form-container"
      >
        <Grid item xs={12} sm={6} md={4} lg={3.5}>
          <Card>
            <CardContent>
              <span>
                <img
                  src="plus.jpg"
                  alt=""
                  height={35}
                  width={35}
                  style={{
                    position: "relative",
                    right: "-130px",
                    top: "15px",
                  }}
                />
              </span>
              <h1>
                Medi <span style={{ color: "green" }}>Pharma</span>
              </h1>
              <p style={{ marginLeft: "20px" }}>Login To Continue</p>
              <form>
                <TextField
                  label="Username"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                  sx={{
                    borderRadius: "80px",
                    "& .MuiOutlinedInput-root": { borderRadius: "80px" },
                  }}
                />
                <TextField
                  label="Password"
                  type="password"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                  sx={{
                    borderRadius: "80px",
                    "& .MuiOutlinedInput-root": { borderRadius: "80px" },
                  }}
                />
                <div
                  onClick={handleToggle}
                  style={{
                    display: "flex",
                    // justifyContent: "center",
                    // alignItems: "center",
                    margin: "20px 0",
                  }}
                >
                  {isToggled ? (
                    <ToggleOnIcon style={{ fontSize: 50, color: "#538eed" }} />
                  ) : (
                    <ToggleOffIcon style={{ fontSize: 50, color: "gray" }} />
                  )}
                  <span
                    style={{
                      position: "relative",
                      top: "-2px",
                      right: "-10px",
                    }}
                  >
                    <p style={{ cursor: "pointer" }}> Remember Me</p>
                  </span>
                </div>
                <Button
                  onClick={handleClick}
                  variant="contained"
                  color="success"
                  fullWidth
                >
                  Login
                </Button>{" "}
                <br />
                <br />
                <center>
                  _________<span style={{ color: "gray" }}> Powered By</span>{" "}
                  MediPharma________
                </center>
              </form>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Grid>
  );
};
