import React, { useState } from "react";
import { Link } from "react-router-dom";
import { CardContent, Grid, Card, TextField, Button } from "@mui/material";
import { Radio, Radioo } from "./Radioo";
import { useNavigate } from "react-router-dom";
import { Categorylist } from "./Categorylist";

export const Addcategory = () => {
  const navigate = useNavigate();

  // const [name, setName] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    status: "",
  });

  const handleCli = () => {
    navigate("/category", { state: { formData } });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <Card sx={{ backgroundColor: "lightsteelblue" }}>
      <CardContent>
        <Grid container spacing={2}>
          <Grid item xs={3}>
            <h2>Add Category</h2>
          </Grid>

          <Grid item xs={9}></Grid>
          <Grid item xs={12}>
            <Grid container spacing={2}>
              <Grid item xs={6}></Grid>
              <Grid item xs={2}>
                <h3>
                  Category Name<span style={{ color: "red" }}>*</span>
                </h3>
              </Grid>
              <Grid item xs={3}>
                <TextField
                  // setName={setName}
                  // name={name}
                  // onChange={(e) => setName(e.target.value)}
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  variant="outlined"
                  fullWidth
                  label="Category Name"
                  // variant="outlined"
                  fullWidth
                  label="Category Name"
                />
              </Grid>
              <Grid item xs={12}>
                <Grid container spacing={2}>
                  <Grid item xs={6}></Grid>
                  <Grid item xs={2}>
                    <h2>
                      Status <span style={{ color: "red" }}>*</span>
                    </h2>
                  </Grid>
                  <Grid item xs={1.4}>
                    <Radioo
                      name="status"
                      value={formData.status}
                      onChange={handleChange}
                    />
                  </Grid>
                  <Grid item xs={3}></Grid>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
          <Grid item xs={10}></Grid>
          <Grid item xs={2}>
            {/* <Link to="category"> */}
            <Button
              onClick={handleCli}
              variant="contained"
              color="success"
              fullWidth
            >
              {" "}
              Save
            </Button>
            {/* </Link> */}
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};
