import React from "react";
import "./update.css";
import { Grid, Card, CardContent, TextField } from "@mui/material";

export const Update = () => {
  return (
    <Grid container spacing={2}>
      <Grid item xs={4}></Grid>
      <Grid item xs={4}>
        <Grid container spacing={2}>
          <Grid sx={{ backgroundColor: "lightgray" }} item xs={4}>
            <h4>Category Name:-</h4>
          </Grid>
          <Grid sx={{ backgroundColor: "lightgray" }} item xs={8}>
            <TextField
              variant="outlined"
              fullWidth
              label="Enter Category Name"
            />
          </Grid>
          <Grid sx={{ backgroundColor: "lightgray" }} item xs={12}>
            <Button
              component="label"
              role={undefined}
              variant="contained"
              tabIndex={-1}
              startIcon={<CloudUploadIcon />}
            >
              Upload file
              <VisuallyHiddenInput type="file" />
            </Button>
          </Grid>
        </Grid>
      </Grid>
      <Grid item xs={4}></Grid>
    </Grid>
  );
};
